mapper.delete:
DELETE a,b FROM order_info
a JOIN harvested_goods b ON a.id=b.order_id
where a.id = #{id}

sql.where:
1=1

		and a.order_number=#{orderNumber}


		and a.integral=#{integral}


		and a.payment_method=#{paymentMethod}


		and a.order_status=#{orderStatus}


		and a.user_notes=#{userNotes}


		and a.user_id=#{userId}


		and a.total_amount=#{totalAmount}


		and a.create_time=#{createTime}


		and a.update_time=#{updateTime}


		and a.admin_notes=#{adminNotes}


		and a.express_no=#{expressNo}

mapper.select:
select

from order_info a join consignee b on a.consignee_id=b.id join member_user c
on a.user_id=c.id


	order by ${sidx} ${sord}


	limit #{startRow},#{pageSize}

select id as id, order_number as order_number, integral as integral, payment_method as payment_method, order_status as order_status, user_notes as user_notes, total_amount as total_amount, express_no, date_format(create_time,'%Y-%m-%d %H:%i:%s') as create_time, date_format(update_time,'%Y-%m-%d %H:%i:%s') as update_time, user_id as user_id, admin_notes from order_info
order by ${sidx} ${sord}
limit #{startRow},#{pageSize}

select a.id as id, a.order_number as orderNumber, a.integral as integral, a.payment_method as paymentMethod, a.order_status as orderStatus, a.user_notes as userNotes, a.total_amount as totalAmount, a.express_no as express_no, date_format(a.create_time,'%Y-%m-%d %H:%i:%s') as createTime, date_format(a.update_time,'%Y-%m-%d %H:%i:%s') as updateTime, a.user_id as userId, a.admin_notes as admin_notes, b.user_name as userName from order_info a join member_user b on a.user_id=b.id
order by ${sidx} ${sord}
limit #{startRow},#{pageSize}
