mapper.select:
SELECT zg.*, zc.catName, zc.catSort FROM zs_goods zg, zs_goods_category zc WHERE zg.goodsCateId = zc.id AND zg.isAdminRecom=#{isAdminRecom} AND zg.isSale=#{isSale}
AND (zg.goodsName LIKE '%${value}%' OR zg.goodsNo LIKE '%${value}%' OR zc.catName LIKE '%${value}%')
order by zg.id ${sord} limit #{page},#{size}
