mapper.insert:
insert into zs_goods_sku (id, goodsId, skuAttrIds
  )
values (#{id,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, #{skuAttrIds,jdbcType=VARCHAR}
  )

mapper.update:
update zs_goods_sku
set goodsId = #{goodsId,jdbcType=INTEGER},
  skuAttrIds = #{skuAttrIds,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT * FROM  zs_goods_sku_attr zsk WHERE 1=1  
   and zsk.id=#{skuId}
   and zsk.cateId=#{goodsCateId}
   order by zsk.id #{sord}
   limit #{page},#{rows}

SELECT count(1) FROM  zs_goods_sku_attr zsk WHERE 1=1  
 and zsk.id=#{skuId}
 and zsk.cateId=#{goodsCateId}
